declare namespace cc {
    class Point {
        constructor(x: Number, y: Number);

        ctor(x: Number, y: Number);

        x;
        y;

    }

    class Rect {
        constructor(x: Number, y: Number, width: Number, height: Number);

        ctor(x: Number, y: Number, width: Number, height: Number);

        height: Number;
        width: Number;
        x: Number;
        y: Number;

    }

    class Size {
        constructor(width: Number, height: Number);

        ctor(width: Number, height: Number);

        height: Number;
        width: Number;

    }

}
